document.addEventListener('DOMContentLoaded', () => {fetch('assets/dummy-data.json').then(response => response.json()).then(data => {const projectList = document.getElementById('projectList');data.projects.forEach(project => {const card = document.createElement('div');card.className = 'project-card';card.innerHTML = `<h2>${project.title}</h2><p>${project.description}</p><button>Apply</button>`;projectList.appendChild(card);});}).catch(error => console.error('Error fetching data:', error));});